import React from 'react'

export default function Chart(props) {
    const { data } = props;
    const dataArr = [...data]

    const gridColumns = () => {
        return[...dataArr[0][1]].map((el, i)=>'1fr ').join('')
    }
    const gridRows = () => {
        return dataArr.map((el, i)=>'1fr ').join('')
    }

    return (
        <div className='chartContainer' style={{ gridTemplateRows: gridRows(), gridTemplateColumns: gridColumns()}}>Chart</div>
    )
}
