import React from 'react'

export default function Chart(props) {
    const { data } = props;
    const dataArr = [...data]

    const gridColumns = () => {
        return dataArr[0][1].yearsValues.map((el, i) => '1fr ').join('') + '1fr'
    }
    const gridRows = () => {
        return dataArr.map((el, i) => i === 0 ? '3fr':'1fr ').join('')
    }

    return (
        <div className='chartContainer' style={{ gridTemplateRows: gridRows(), gridTemplateColumns: gridColumns() }}>
            {dataArr.map((place, i) => {
                return (
                    <div
                        key={i + place[0]}
                        style={{
                            gridColumn: 1,
                            gridRow: i
                        }}
                        className='placeName'
                    >
                        {place[0]}
                    </div>
                )


            })}

            {dataArr.map((place, i) => {
                const yearsValues = place[1].yearsValues;
                return yearsValues.map((value, j) => {
                    return (
                        <div
                            key={`${i}${j}${value}`}
                            style={{
                                backgroundColor: (value === 'x' ? 'grey' : `hsl(${value}, 30%, ${60 + value * 2.1}%)`),
                                gridColumn: (2 + j),
                                gridRow: i
                            }}
                            className='placeArea'
                        >
                            {value}
                        </div>
                    )
                }
                )
            })}
        </div>
    )
}
